import {
    FETCH_ROBOTS_START,
    FETCH_ROBOTS_SUCCESS,
    FETCH_ROBOTS_FAILURE,
    FETCH_ROBOT_BY_ID_START,
    FETCH_ROBOT_BY_ID_SUCCESS,
    FETCH_ROBOT_BY_ID_FAILURE
} from 'actionTypes'

import {fetchRobots as fetchRobotsApi,
        fetchRobotById as fetchRobotByIdApi
} from 'api'

export const fetchRobots = () => async dispatch => {
    dispatch({type: FETCH_ROBOTS_START})

    try {
        const robots = await fetchRobotsApi()
        dispatch({
            type: FETCH_ROBOTS_SUCCESS,
            payload: robots
        })
    } catch (err) {
        dispatch({
            type: FETCH_ROBOTS_FAILURE,
            payload: err,
            error: true
        })
    }
}

export const fetchRobotById = id => async dispatch => {
    dispatch({type: FETCH_ROBOT_BY_ID_START})

    try {
        const robot = await fetchRobotByIdApi(id)
        dispatch({
            type: FETCH_ROBOT_BY_ID_SUCCESS,
            payload: robot
        })
    } catch(err) {
        dispatch({
            type: FETCH_ROBOT_BY_ID_FAILURE,
            payload: err,
            error: true
        })
    }
}