import {
    FETCH_ROBOTS_START,
    FETCH_ROBOTS_SUCCESS,
    FETCH_ROBOTS_FAILURE
} from 'actionTypes'

import {fetchRobots as fetchRobotsApi} from 'api'

export const fetchRobots = () => async dispatch => {
    dispatch({type: FETCH_ROBOTS_START})

    try {
        const robots = await fetchRobotsApi()
        dispatch({
            type: FETCH_ROBOTS_SUCCESS,
            payload: robots
        })
    } catch (err) {
        dispatch({
            type: FETCH_ROBOTS_FAILURE,
            payload: err,
            error: true
        })
    }
}