import robots from './mockRobots'
import R from 'ramda'

export const fetchRobots = async () => {
    return new Promise(resolve => {
        resolve(robots)
    })
}

export const fetchRobotById = async id => {
    return new Promise((resolve, reject) => {
        const robot = R.find(R.propEq('id', id), robots)
        resolve(robot)
    })
}