import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router'

import {fetchRobots} from 'actions'
import {getRobots} from 'selectors'

class Robots extends Component {
    componentDidMount() {
        this.props.fetchRobots()
    }

    renderRobot (robot, index) {
        return (
            <Link to={`/robots/${robot.id}`} className="col-lg-3 col-md-4 col-sm-6 col-xs-12" key={index}>
                <div className="robot-item" style={{backgroundImage: `url(${robot.image})`}}>
                    <span className="robot-item-name">{robot.name}</span>
                    <button className="robot-item-button">{robot.price} $</button>
                </div>
            </Link>
        )
    }

    render() {
        const {robots} = this.props
        return (
            <div className="container">
                <div className="row">
                        {robots.map((robot, i) => this.renderRobot(robot, i))}
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    robots: getRobots(state)
})
const mapDispatchToProps = {
    fetchRobots
}

export default connect(mapStateToProps, mapDispatchToProps)(Robots)